mapper.insert:
insert into tbl_history( num, wikinum, usernum, explanation, type, content
,revertnum
)values( seq_history.nextval, #{wikinum}, #{usernum}, #{explanation}, #{type}, #{content}
,#{revertnum}
)

mapper.select:
select count(*)
from tbl_history
where wikinum = #{wikinum}

select *
from (
	select *
	from tbl_history
	where wikinum = #{wikinum}
	order by num desc
)
where rownum = 1
