mapper.insert:
insert into web5_member(
	id, 
	password, 
	name

	, phone


	,address 


	,email

) values(
	#{id},
	#{password},
	#{name}

		,#{phone}


		,#{address}


		,#{email}

)

mapper.select:
select id, name, phone, address, email
from web5_member
where id = #{id} and password = #{password}

select count(*)
from web5_member
where id = #{id}

select id, name, phone, address, email
from web5_member
where id = #{id}

